			if (this.result.from >= value)
			  this.setFromValue (value );
			else
				if (this.result.to <= value)
				  this.setToValue (value );
				else
					if (Math.abs( this.result.from - value ) >= Math.abs( this.result.to - value ))
						this.setToValue( value );
