					this.coords.p_gap = this.toFixed(this.coords.p_pointer - this.coords.p_from);
					this.$cache.s_from.addClass("state_hover");
					this.$cache.s_from.addClass("type_last");
					this.$cache.s_to.removeClass("type_last");
